UPDATE books set slides = '[{"page":0,"letter":0,"subletter":1,"content":"\u003ch3\u003eדרוש א\u0026#39;:\u003c/h3\u003e"},{"page":0,"letter":0,"subletter":2,"content":"\u003ch3\u003eבענין חנוכה\u003c/h3\u003e"},{"page":0,"letter":0,"subletter":3,"content":"סדר הדלקת הנרות היה נוהג כנוסח הכתוב בבית יוסף ז\"ל על \u003cbr/\u003eהטורים וגם בספר שולחן ערוך. והוא כי בלילה הא ידליק הנר שבצד \u003cbr/\u003eימינו שהוא היותר רחוק מן הפתח מכל ז\u0026#39; נרות ובליל ב\u0026#39; ידליק נר הב\u0026#39; \u003cbr/\u003eלו שהוא יותר"},{"page":0,"letter":0,"subletter":4,"content":"סמוך אל הפתח יותר מן הא\u0026#39; ואח\"כ יפנה אל ימין וידליק הנר הא\u0026#39; \u003cbr/\u003eוכעד\"ז כל שאר הלילות עד שנמצא כי בלילה האחרונה מדליק הנר \u003cbr/\u003eשבצד שמאלו "},{"page":0,"letter":0,"subletter":5,"content":"שהוא הנר היותר קרוב וסמוך אל הפתח ומתחיל בו ונפנה לצד ימינו \u003cbr/\u003eהקרוב קרוב קודם ונמצא כי הנר הא\u0026#39; מכולם שהוא בצד ימין המדליק \u003cbr/\u003eנדלק אחרון בלילה אחרונה. "},{"page":0,"letter":0,"subletter":6,"content":"ואמנם נבאר תחילה ענין קדושת הימים האלו ואח\"כ נבאר ענין ברכות \u003cbr/\u003eנר של חנוכה והנה כמו שנתבאר שבשבתות ויו\"ט ור\"ח יש בהם \u003cbr/\u003eתוספת קדושה על ימי החול "},{"page":0,"letter":0,"subletter":7,"content":"גם בחנוכה ופורים יש בהם תוספת קדושה יותר מימי החול אמנם אין \u003cbr/\u003eמדריגתם שוה למדריגת השבתות ויו\"ט ור\"ח והנה הם נתבארו כל \u003cbr/\u003eאחד במקומו. "},{"page":0,"letter":0,"subletter":8,"content":"אבל ענין חנוכה ופורים הוא באופן אחר כי תרוייהו אינון בהוד ואעפ\"י \u003cbr/\u003eשבתפלת שחרית בחול ביארנו דיעקב בנצח ומלכות בהוד וכפ\"ז החול \u003cbr/\u003eהוא קדוש יותר מחנוכה ופורים אשר תרוייהו אינון בהוד "},{"page":0,"letter":0,"subletter":9,"content":"אבל הענין הוא על דרך שביארנו בדרוש ר\"ח בענין הי\"ט כי הנוק\u0026#39; יש \u003cbr/\u003eלה אז תוספות קדושה לפי שאז מקבלת הארותיה ע\"י עצמה שלא ע\"י \u003cbr/\u003eבעלה וכן הוא כאן "},{"page":0,"letter":0,"subletter":10,"content":"כי הנה בחול יעקב יונק הארתו וחלק הארה שלה מן הנצח ומן ההוד \u003cbr/\u003eואח\"כ נוחן לה הארתה ונמשך מן ההוד על ידו אבל עתה בחנוכה \u003cbr/\u003eופורים היא יונקת חלק הארותיה מן ההוד ע\"י עצמה "},{"page":0,"letter":0,"subletter":11,"content":"שלא ע\"י בעלה ואינה טפילה אליו כימי החול והנה ענין זה הוא \u003cbr/\u003eתוספת קדושה אליה בזמנים אלו יותר מבחול:"},{"page":0,"letter":0,"subletter":12,"content":"ואמנם ענין כוונת ברכות הדלקת הנר הם סובבים על יחוד א\u0026#39; עליון \u003cbr/\u003eושלם הנקרא נר כמבואר אצלינו בתפילת שחרית דחול בברכת שים \u003cbr/\u003eשלום כי שם נתבאר יחוד זה על מתכונתו ועיין שם. "},{"page":0,"letter":0,"subletter":13,"content":"ועניינו בקיצור הוא כי שלשה בחי\u0026#39; של יחוד יש לזו\"ן א\u0026#39; הוא בבחי\u0026#39; ידוד \u003cbr/\u003eאהיה ומספרו מ\"ז הב\u0026#39; הוא בבחי\u0026#39; ידוד אלקים ומספרו יב\"ק. "},{"page":0,"letter":0,"subletter":14,"content":"הג\u0026#39; הוא בבחי\u0026#39; ידוד אדני ומספרו צ\"א ולפעמים מתייחדים בבחי\u0026#39; א\u0026#39; \u003cbr/\u003eולפעמים בשתיה\u0026#39; ולפעמים בשלשתם ואז הוא היחוד הגמור ואז הנוק\u0026#39; \u003cbr/\u003eנקרא נ\"ר כמספר ו\u0026#39; שמות הנז\u0026#39;. "},{"page":0,"letter":0,"subletter":15,"content":"והנה בברכה הא שהיא להדליק נר חנוכה נרמזו שלשתם ובברכה ב\u0026#39; \u003cbr/\u003eשהיא שעשה נסים נרמזה הב\u0026#39; ובברכה הג\u0026#39; שהיא שהחיינו נרמזה \u003cbr/\u003eהתחתונה מכולם "},{"page":0,"letter":0,"subletter":16,"content":"וזהו מה שמצאתי בקונטריס אחר כי ההויה הא\u0026#39; שעם אהי\"ה תהיה \u003cbr/\u003eבמילוי יודין דע\"ב וההויה הב\u0026#39; שעם אלקים היא דס\"ג וההויה הג\u0026#39; שעם \u003cbr/\u003eאדני היא הויה דמ\"ה וג\u0026#39; הויות אלו עצמם הם שתכוין במילת להדליק "},{"page":0,"letter":0,"subletter":17,"content":"שהיא בגי\u0026#39; ע\"ב ס\"ג מ\"ה של להדליק ומן האהי\"ה שבחיבו\u0026#39; ההויה \u003cbr/\u003eדע\"ב דיודין גם הוא יהיה במילוי יודין "},{"page":0,"letter":0,"subletter":18,"content":"שהוא קס\"א ועם ההויה עצמה שהיא ע\"ב יהיו שניהם בגי\u0026#39; רג\"ל לרמוז \u003cbr/\u003eכי גם חנוכה נקר\u0026#39; רג\"ל מכלל המועדים כנז\u0026#39; בכתיבת יד בהקדמת \u003cbr/\u003eהתיקונים:"},{"page":0,"letter":0,"subletter":19,"content":"ואמנם בקונטריס אדם אחר מצאתי כי בברכה א\u0026#39; של להדליק כשיאמר \u003cbr/\u003eברוך אתה ידוד יכוין שזו ההויה היא דע\"ב דיודין ותכוין לחבר עמו גם \u003cbr/\u003eאהיה דיודין ושניהם בגימט\u0026#39; רג\"ל "},{"page":0,"letter":0,"subletter":20,"content":"וז\"ס עד שתכלה רגל מן השוק. ובברכת שעשה נסים יכוין באות\u0026#39; הויה \u003cbr/\u003eלמילוי ס\"ג ויחבר עמו שם אלקים ולכן גם ההויה דס\"ג יכוין שהוא \u003cbr/\u003eבניקו\u0026#39; אלקים. "},{"page":0,"letter":0,"subletter":21,"content":"ובברכת שהחיינו יכוין באותה ההויה שהוא במילוי מ\"ה דאלפין ויחבר \u003cbr/\u003eעמה שם אדני:"},{"page":0,"letter":0,"subletter":22,"content":"ונחזור לברכה ראשונה כמו שאמרנו כי בתחילה תכוין במילת להדליק \u003cbr/\u003eשהוא בגי\u0026#39; ג\u0026#39; הויות ע\"ב ס\"ג מ\"ה ותכוין להמשיך ג\u0026#39; הויות האלו ע\"י \u003cbr/\u003eהדלקה זו אל הנוקבא הנקרא הויה דב\"ן. "},{"page":0,"letter":0,"subletter":23,"content":"ובמילת נר תכוין שע\"י המשכה זו תשתלם בג\u0026#39; היחודים הנ\"ל ואז \u003cbr/\u003eתעשה בחי\u0026#39; נר חנוכה שהוא כמנין ג\u0026#39; היחודים הנז\u0026#39; ותתייחד עם בעלה \u003cbr/\u003eבג\u0026#39; היחודים הנז\u0026#39;. "},{"page":0,"letter":0,"subletter":24,"content":"אח\"כ תכוין כי ר\"ת להדליק נר חנוכה הוא שם קדוש הנקרא נחל \u003cbr/\u003eהיוצא מר\"ת נוצר חסד לאלפים כנודע. "},{"page":0,"letter":0,"subletter":25,"content":"ועניינו הוא אור הנמשך מן אימא לז\"א כדי שיהיה בו כח להתייחד \u003cbr/\u003eעמה בג\u0026#39; היחודים הנז\u0026#39; שהם בגימ\u0026#39; נר ולכן תכוין בשם נחל שהוא בגי\u0026#39; \u003cbr/\u003eהויה דמ\"ה דאלפין "},{"page":0,"letter":0,"subletter":26,"content":"ואות אלף שבאמצע מילוי אות ו\u0026#39; היא שם אהיה בריבועו כזה יוד ה\"א \u003cbr/\u003eו\u0026#39; א\u0026#39; א\"ה אה\"י אהי\"ה ו\u0026#39; ה\"א כי שם זה מורה ענין אימא המאירה בז\"א \u003cbr/\u003eבהתלבשה בתוכו "},{"page":0,"letter":0,"subletter":27,"content":"ונלע\"ד ששמעתי שיכוין ג\"כ שהנחל הנז\u0026#39; שהוא הויה דמ\"ה ובתוכו \u003cbr/\u003eריבוע של אהיה ימשך בנוק\u0026#39; הנקרא הויה דב\"ן אשר אותיותיה \u003cbr/\u003eשבמילוי המילוי הם ל\"ו אותיות "},{"page":0,"letter":0,"subletter":28,"content":"והנה ב\"ן ול\"ו הם ג\"כ בגי\u0026#39; נח\"ל ואיני זוכר אם שמעתי זה ממורי ז\"ל:"},{"page":0,"letter":0,"subletter":29,"content":"אח\"כ במלת חנוכה תכוין כי כמו שהמשכנו הארה מאי\u0026#39; אל ז\"א בשם \u003cbr/\u003eנחל כדי שיוכל להתייחד עמה בג\u0026#39; יחודי\u0026#39; הנקר\u0026#39; נר כן עתה נמשיך הויה \u003cbr/\u003eדס\"ג של אי\u0026#39; "},{"page":0,"letter":0,"subletter":30,"content":"שהוא מספר עשר אותיו\u0026#39; המילוי עם חשבון אותיו\u0026#39; הפשוט לבדם שהם \u003cbr/\u003eכ\"ו והרי כ\"ו וס\"ג בגי\u0026#39; חנוכה וזהו ההמשכה תמשיכנו בנוק\u0026#39; הנק\u0026#39; נר \u003cbr/\u003eועי\"כ תהיה נר חנוכה. "},{"page":0,"letter":0,"subletter":31,"content":"עוד יכוין במילת חנוכה אל מ\"ש חז\"ל שהוא אותיות חנותכה והם סוד \u003cbr/\u003eכ\"ה אותיות שיש בו\u0026#39; השמות שאמרנו שיכוין במלת נר:"},{"page":0,"letter":0,"subletter":32,"content":"ברכה ב\u0026#39; והוא שעשה נסים כו\u0026#39; במלת שעשה תכוין שהוא חיבור ש\"ע \u003cbr/\u003eש\"ה פירוש שתכוין להמשיך הארת הפנים העליונים שהם ב\u0026#39; שמות \u003cbr/\u003eאל ומילוייהם "},{"page":0,"letter":0,"subletter":33,"content":"כזה אלף למד אלף למד העולים בגי\u0026#39; ש\"ע כנז\u0026#39; באורך בתפילת ר\"ה \u003cbr/\u003eבברכת אבות במלת אלקי אברהם אלקי יצחק ואלקי יעקב כו\u0026#39; וע\"ש \u003cbr/\u003eותמשיכם אל שם אלקים דמילוי יודין "},{"page":0,"letter":0,"subletter":34,"content":"העולה בגי\u0026#39; ש\u0026#39; ועם ה\u0026#39; אותיותיו הפשוטות ה\u0026#39; הרי ש\"ה מן שעשה ש\"ע \u003cbr/\u003eש\"ה ובזה יתמתק שם אלקים הנז\u0026#39;. "},{"page":0,"letter":0,"subletter":35,"content":"עוד ירצה כי ענין ב\u0026#39; שמות אלו של אל הם סוד הויה דס\"ג שביארנו \u003cbr/\u003eבמלת חנוכה שהוא כ\"ו וס\"ג שהוא הויה בפשוט ובמילוי. "},{"page":0,"letter":0,"subletter":36,"content":"והנה בפשוט הוא אל כי חשבונה הוא כ\"ו וד\u0026#39; אותיותי\u0026#39; והכולל הרי אל \u003cbr/\u003eוגם במילואה דס\"ג "},{"page":0,"letter":0,"subletter":37,"content":"יש בו אותיות ייאי שהוא בגי\u0026#39; אל ואלו ב\u0026#39; שמות אל מתמלאים והם בגי\u0026#39; \u003cbr/\u003eש\"ע מן שעשה כנז\"ל ולא נמצא תשלום הדרוש הזה. עוד מצאתי \u003cbr/\u003eבספר אחר על ענין חנוכה וז\"ל:"},{"page":0,"letter":0,"subletter":38,"content":"דע כי מה שאנו צריכין לתקן הוא זווג זו\"ן ויש כמה בחינות כי כמו \u003cbr/\u003eשהאצילות יש בו ג\u0026#39; חלקים והוא קוצו של יוד ואין משיגים בו אלא \u003cbr/\u003eמהחיצוניו\u0026#39; שבו שהוא הקרקפתא העצם המקיף המוחין ואינו אלא \u003cbr/\u003eהלבוש וזה"},{"page":0,"letter":0,"subletter":39,"content":"הלבוש בלבד אנו משיגים. והבחי\u0026#39; הב\u0026#39; מן המוחין דאו\"א ודעת ובאלו \u003cbr/\u003eאנו משיגים הפנימיו\u0026#39; עצמן ואע\"פ שהם שלשה כולם תלוים באבא \u003cbr/\u003eונקרא על שמו כי סתם מוח הוא מאבא. "},{"page":0,"letter":0,"subletter":40,"content":"הבחי\u0026#39; הג\u0026#39; הם ז\u0026#39; מידות התחתונות שמהם הנהגות כל העולמות וכן יש \u003cbr/\u003eבזווג זו\"ן ג\u0026#39; בחי\u0026#39; א\u0026#39; ו\"ק ופי\u0026#39; כשהיא עולה בו\"ק שלו ושם הם מזדווגות \u003cbr/\u003eוזה אינו חשוב כ\"כ "},{"page":0,"letter":0,"subletter":41,"content":"לפי שרמ\"ח אבריה הם מו\"ק שלו ולמטה ועוד שאינם פב\"פ ועוד כי \u003cbr/\u003eהחיבוק אינו בגופה אלא בתחילת ראשה ואינה בין תרין דרועין \u003cbr/\u003eדמלכא. "},{"page":0,"letter":0,"subletter":42,"content":"הבחי\u0026#39; הב\u0026#39; כשעולה עד המוחין שלו ושם מזדווגת וגם זווג זה גם \u003cbr/\u003eשהוא מעולה מן הא\u0026#39; אינו חשוב כ\"כ לפי שהכתר שלה הוא כנגד \u003cbr/\u003eהפנים שלו ואינם פב\"פ והחיבוק בצואר. "},{"page":0,"letter":0,"subletter":43,"content":"הבחי\u0026#39; הג\u0026#39; כשהיא עולה עד הכתר שלו זהו מובחר כי הוא פב\"פ \u003cbr/\u003eוהחיבוק הוא בגופ\u0026#39; ממש ואז היא נקר\u0026#39; נ\"ר כמ\"ש בזוהר רמ\"ח איברים \u003cbr/\u003eותרין דרועין דיליה המחבקים אותה הם נ\"ר. "},{"page":0,"letter":0,"subletter":44,"content":"וזהו נר שבת ונר חנוכה אלא שיש הפרש ביניהם כי נר שבת עולה \u003cbr/\u003eהיא למעלה עד הכתר שלו ממש ונר חנוכה הוא אשר יורדים כל הבחי\u0026#39; \u003cbr/\u003eאלו אליה למטה במקומה "},{"page":0,"letter":0,"subletter":45,"content":"ולכן קניית שמן של שבת קודם ובענין ההדלקה נר חנוכה קודם כי אם \u003cbr/\u003eידלוק של שבת קודם כבר עלתה ומה התועלת באחרת אבל יעשו לה \u003cbr/\u003eהכנה בהדלקת נר חנוכה וירדו בה האורות ואחר כך תעלה בנר שבת:"}]'
WHERE id = 1388;

ALTER TABLE books RENAME TO old_books;

ALTER TABLE bookmarks RENAME TO old_bookmarks;

CREATE TABLE IF NOT EXISTS books (
    id SERIAL PRIMARY KEY,
    author VARCHAR(50),
    title VARCHAR(150)
);

CREATE TABLE IF NOT EXISTS contents (
    id SERIAL PRIMARY KEY,
    book_id INT REFERENCES books (id),
    content VARCHAR,
    page INT
);

CREATE TABLE IF NOT EXISTS bookmarks (
    id SERIAL PRIMARY KEY,
    book_id INT REFERENCES books (id),
    content_id INT REFERENCES contents (id),
    path VARCHAR(50) 
);

INSERT INTO books (id, author, title)
SELECT id, author, title
FROM old_books;

INSERT INTO contents (book_id, content)
SELECT id, content
FROM old_books;

INSERT INTO bookmarks (book_id, path)
SELECT DISTINCT id, book
FROM 
(SELECT DISTINCT old_books.id, old_bookmarks.book
FROM old_bookmarks
RIGHT JOIN old_books ON old_books.author = old_bookmarks.author) AS bm
WHERE bm.book IS NOT NULL;

UPDATE bookmarks as bm SET content_id = c.id
FROM contents c 
WHERE bm.book_id = c.book_id;

UPDATE contents as c SET page = bp.page
FROM 
(select id, page::int
from
(select id, slides::json->0->>'page'::text as page
FROM old_books) as obp) as bp 
WHERE c.book_id = bp.id;